mapper.select:
/** getLoginInfo **/
SELECT uid, login_id as loginId, name, `password`, email, phone , date_format(reg_dt, '%Y-%m-%d %H:%i:%s') as regDt, date_format(upt_dt, '%Y-%m-%d %H:%i:%s') as uptDt
FROM project.`user`
WHERE 1=1

    AND login_id =#{loginId}

/** getLoginInfo **/
SELECT uid, login_id as loginId, name, `password`, email, phone, date_format(reg_dt, '%Y-%m-%d %H:%i:%s') as regDt, date_format(upt_dt, '%Y-%m-%d %H:%i:%s') as uptDt
FROM project.`user`
WHERE 1=1

    AND login_id =#{loginId}


    AND password =#{password}
